package com.byzoro;import java.io.File;import java.io.IOException;/** * @author huanyuguo * @date 2018/4/28 9:23 */public class Main {    public static void main(String[] args) {        File file = new File("D:\\video\\test123.mp4");        try {            boolean newFile = file.createNewFile();            if (newFile){                System.out.println(file.getName()+"创建成功");            }else{                System.out.println(file.getName()+"已经存在");            }        } catch (IOException e) {            e.printStackTrace();        }        System.out.println(file.getName()+"的绝对路径是"+file.getAbsolutePath());    }    public static void printFilePath(String pathName){        File file = new File(pathName);        System.out.println(file.getName());        System.out.println(file.exists());        System.out.println(file.getAbsolutePath());        try {            System.out.println(file.getCanonicalPath());        } catch (IOException e) {            e.printStackTrace();        }    }}