package com.byzoro;import java.io.File;import java.io.IOException;/** * @author huanyuguo * @date 2018/4/28 9:23 */public class Main {    public static void main(String[] args) {        System.out.println(File.separator);        printFilePath("D:\\video\\test.mp4");        System.out.println("=================================");        printFilePath(".." + File.separator + "notes.txt");    }    public static void printFilePath(String pathName){        File file = new File(pathName);        System.out.println(file.getName());        System.out.println(file.exists());        System.out.println(file.getAbsolutePath());        try {            System.out.println(file.getCanonicalPath());        } catch (IOException e) {            e.printStackTrace();        }    }}