package com.byzoro;/** * @author huanyuguo * @date 2018/5/4 20:14 */public class MyCurrentThread extends Thread {    public MyCurrentThread(String name){        super(name);    }    @Override    public void run(){        Thread thread = Thread.currentThread();        String threadName = thread.getName();        System.out.println("Inside run() method:  " + threadName);    }    public static void main(String[] args) {        MyCurrentThread first_thread = new MyCurrentThread("First Thread");        MyCurrentThread second_thread = new MyCurrentThread("Second Thread");        first_thread.start();        second_thread.start();        Thread thread = Thread.currentThread();        String currentThreadName = thread.getName();        System.out.println("Inside main() method:  " + currentThreadName);    }}