package com.byzoro.dataStructure;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;/** * @author huanyuguo * @date 2018/5/31 9:24 */public class strToDateFormat {    public static String strToDateFormat(String date) throws ParseException {        SimpleDateFormat formatter = new SimpleDateFormat("yyyyMMdd");        formatter.setLenient(false);        Date newDate= formatter.parse(date);        formatter = new SimpleDateFormat("yyyy-MM-dd");        System.out.println(formatter.format(newDate));        return formatter.format(newDate);    }    public static void main(String[] args) {        try {            strToDateFormat("20150330");        } catch (ParseException e) {            e.printStackTrace();        }    }}