package com.byzoro.thread;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;/** * @author huanyuguo * @date 2018/5/20 16:37 */public class testCachedThreadPool {    public static void main(String[] args) {        ExecutorService exec = Executors.newCachedThreadPool();        for (int i = 0; i < 5; i++) {            exec.execute(new LiftOff());        }        exec.shutdown();    }}