package com.byzoro.thread;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;/** * @author huanyuguo * @date 2018/5/20 16:41 */public class testFixedThreadPool {    public static void main(String[] args) {        ExecutorService exec = Executors.newFixedThreadPool(5);        for (int i = 0; i < 5; i++) {            exec.execute(new LiftOff());        }        exec.shutdown();    }}