package com.byzoro.thread;/** * @author huanyuguo * @date 2018/5/21 11:31 */class Sleeper extends Thread {    private int duration;    public Sleeper(String name, int sleepTime) {        super(name);        this.duration = sleepTime;        start();    }    @Override    public void run() {        try {            sleep(duration);        } catch (InterruptedException e) {            System.out.println(getName() + " was interrupted. " +                    "isInterrupted(): " + isInterrupted());            return;        }        System.out.println(getName() + " has awakened");    }}class Joiner extends Thread {    private Sleeper sleeper;    public Joiner(String name, Sleeper sleeper) {        super(name);        this.sleeper = sleeper;        start();    }    @Override    public void run() {        try {            sleeper.join();        } catch (InterruptedException e) {            System.out.println("Interrupted");        }        System.out.println(getName() + " join completed");    }}public class testJoin {    public static void main(String[] args) {        Sleeper sleepy = new Sleeper("Sleepy", 1500);        Sleeper grumpy = new Sleeper("Grumpy", 1500);        new Joiner("Doc1",sleepy);        new Joiner("Doc2",grumpy);        grumpy.interrupt();    }}