package com.byzoro.thread;import java.util.concurrent.TimeUnit;/** * @Describe TODO: 后台线程的测试   main为非后台线程，非后台线程结束后，程序就终止，同时杀死所有后台线程； * @author huanyuguo * @date 2018/5/21 10:59 *//** * @Describe * @Param * @return * @author huanyuguo * @date 2018/5/21 11:15**/public class testSimpleDaemon implements Runnable {    @Override    public void run() {        try {            while (true) {                TimeUnit.MILLISECONDS.sleep(100);                System.out.println(Thread.currentThread() + " " + this);            }        } catch (InterruptedException e) {            System.out.print("sleep() interrupted");        }    }    public static void main(String[] args) throws InterruptedException {        for (int i = 0; i < 10; i++) {            Thread daemon = new Thread(new testSimpleDaemon());            daemon.setDaemon(true);            daemon.start();        }        System.out.println("all daemon started");        TimeUnit.MILLISECONDS.sleep(175);    }}