package com.byzoro.thread;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;import java.util.concurrent.TimeUnit;/** * @Describe TODO: sleeping测试 * @author huanyuguo * @date 2018/5/21 8:05 */public class testSleepingTask extends LiftOff {    @Override    public void run() {        try {            while (countDown-- > 0) {                System.out.print(status());                TimeUnit.MILLISECONDS.sleep(100);            }        } catch (InterruptedException e) {            e.printStackTrace();            System.err.println("Interrupted");        }    }    public static void main(String[] args) {        ExecutorService exec = Executors.newCachedThreadPool();        for (int i = 0; i < 5; i++) {            exec.execute(new testSleepingTask());        }        exec.shutdown();    }}